import React, { Component } from 'react';
import axios from 'axios';
import { 
  Table
} from 'semantic-ui-form';

class Inventory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          title: null,
          cost: null,
          description: null,
          quantity: null,
          price: null,
          imageURLs: null
        }
      ]
    }
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  componentDidMount() {
    this.fetchItems();
  }

  fetchItems() {

  }

  // Generic function to handle simple updates to the state. Used for <input> components
  handleInputChange = (item) => {
    const target = item.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  };
  
  render() { 
    return (
      <div>
        <Table>
          <Table.Header></Table.Header>
        </Table>
      </div>
    )
  }
}

export default Inventory;
