import React from 'react';
import { Link } from 'react-router-dom';
import { 
  Sidebar,
  Segment,
  Menu,
  Icon,
  Grid,
  Checkbox,
  Button
} from 'semantic-ui-react';

class Sidenav extends React.Component {
  constructor() {
    super();
    this.state = {
      visible: false,
      setVisible: false
    }
    this.handleToggle = this.handleToggle.bind(this);
  };
  
  handleToggle() {
    this.setState({
      visible: !this.state.visible
    })
  }

  render() {
    return(
      <Grid columns={1}>
        <Grid.Column>
          <Button onClick={this.handleToggle}>Click</Button>
        </Grid.Column>

        <Grid.Column>
          <Sidebar.Pushable as={Segment}>
            <Sidebar
              as={Menu}
              animation='push'
              icon='labeled'
              inverted
              onHide={() => this.setState({setVisible: false})}
              vertical
              visible={this.state.visible}
              width='wide'
            >
              <Menu.Item as='a'><Icon name='home' /><Link path='/'> Dashboard </Link></Menu.Item>
            <Menu.Item as='a'>
              <Icon name='gamepad' />
              Games
            </Menu.Item>
            <Menu.Item as='a'>
              <Icon name='camera' />
              Channels
            </Menu.Item>
            </Sidebar>
          </Sidebar.Pushable>
        </Grid.Column>
      </Grid>
    )
  }
}

export default Sidenav;
